package com.vmetry.test;

public class DataTypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;
		String s;
		char c;
		float f;
		boolean b;
		age = 25;
		s = "Shanjay";
		c = 'S';
		f = 30000.00f;
		b = true;
		System.out.println("Age" + age);
		System.out.println("Name:" + s);
		System.out.println("First letter of my name is:" + c);
		System.out.println("My Salary is:" + f);
		System.out.println("Am I a Software professional:" + b);

	}

}
